// src/components/ProblemTable.js

import React, { useState } from "react";
import "./ProblemTable.css"; // Import your CSS file for styling

function ProblemTable({ problems }) {
  // Create an object to group problems by rating
  const groupedProblems = {};

  // Group problems by rating
  problems.forEach((problem) => {
    if (!groupedProblems[problem.rating]) {
      groupedProblems[problem.rating] = [];
    }
    groupedProblems[problem.rating].push(problem.problemId);
  });

  // State to track which rating is currently expanded
  const [expandedRating, setExpandedRating] = useState(null);

  const handleRatingClick = (rating) => {
    if (expandedRating === rating) {
      // If the clicked rating is already expanded, collapse it
      setExpandedRating(null);
    } else {
      // Otherwise, expand the clicked rating
      setExpandedRating(rating);
    }
  };

  return (
    <div>
      <h2>Problems by Rating</h2>
      <div className="rating-tags">
        {Object.keys(groupedProblems).map((rating) => (
          <button
            key={rating}
            onClick={() => handleRatingClick(rating)}
            className={expandedRating === rating ? "active" : ""}
          >
            Rating: {rating}
          </button>
        ))}
      </div>
      <ul>
        {expandedRating !== null &&
          groupedProblems[expandedRating].map((problemId) => (
            <a>
              <li key={problemId}>{problemId}</li>
            </a>
          ))}
      </ul>
    </div>
  );
}

export default ProblemTable;
